protected void Form_addRoom_ItemInserting(object sender, FormViewInsertEventArgs e)\n{\n    // Retrieve the entered value from the FormView control\n    int enteredMaxPeople = Convert.ToInt32(e.Values["MaxPeople"]);\n\n    // Query the database to get the maximum allowed people (pseudo code)\n    int queryMaxPeople = GetMaxPeopleFromDatabase();\n\n    // Check the condition and cancel the insert if necessary\n    if (enteredMaxPeople > queryMaxPeople)\n    {\n        // Cancel the insert operation\n        e.Cancel = true;\n\n        // Display a message to the user\n        Label statusLabel = (Label)Form_addRoom.FindControl("statusLabel");\n        if (statusLabel != null)\n        {\n            statusLabel.Text = "Value entered not valid. Maximum people allowed: " + queryMaxPeople;\n        }\n    }\n}\n\n// Pseudo method for database query\nprivate int GetMaxPeopleFromDatabase()\n{\n    // Implement your database query logic here to retrieve the maximum people allowed\n    // For example, you can use ADO.NET or Entity Framework to query the database\n    // Returning a dummy value for illustration purposes\n    return 100;\n}