using System;\nusing System.Windows.Forms;\n\npublic class KeyConverter\n{\n    public static Keys ConvertVirtualKeyCodeToKeys(int virtualKeyCode)\n    {\n        return (Keys)virtualKeyCode;\n    }\n\n    public static void Main()\n    {\n        int virtualKeyCode = 0x41; // Example Virtual Key code (A key)\n        Keys key = ConvertVirtualKeyCodeToKeys(virtualKeyCode);\n        Console.WriteLine(key); // Outputs: A\n    }\n}