using System.Collections.Generic;\nusing System.Collections.Specialized;\n\npublic class StringCollectionConverter\n{\n    public static List<string> ConvertToList(StringCollection stringCollection)\n    {\n        List<string> stringList = new List<string>();\n\n        foreach (string str in stringCollection)\n        {\n            stringList.Add(str);\n        }\n\n        return stringList;\n    }\n}