using System;\n\nclass Program \n{\n    static void Main(string[] args) \n    {\n        const long n = 600851475143;\n        long num = n;\n        long largestPrime = 0;\n\n        for (long i = 2; i * i <= num; i++) \n        {\n            while (num % i == 0) \n            {\n                largestPrime = i;\n                num /= i;\n            }\n        }\n\n        if (num > 1) \n        {\n            largestPrime = num;\n        }\n\n        Console.WriteLine("The largest prime factor is " + largestPrime + ".");\n    }\n}