public class Message\n{\n    public Guid Id { get; set; }\n    public string Content { get; set; }\n}\n\npublic async Task SendMessageAsync(Message message)\n{\n    using (var httpClient = new HttpClient())\n    {\n        string url = "https://companyA.example.com/api/messages";\n        var jsonContent = new StringContent(JsonConvert.SerializeObject(message), Encoding.UTF8, "application/json");\n        HttpResponseMessage response = await httpClient.PostAsync(url, jsonContent);\n\n        if (response.IsSuccessStatusCode)\n        {\n            Console.WriteLine("Message sent successfully and acknowledged.");\n        }\n        else\n        {\n            Console.WriteLine("Failed to send message. Retry logic can be implemented here.");\n        }\n    }\n}