public class Message\n{\n    public Guid Id { get; set; }\n    public string Content { get; set; }\n}\n\n[HttpPost]\n[Route("api/messages")]\npublic async Task<IActionResult> ReceiveMessage([FromBody] Message message)\n{\n    if (message == null || message.Id == Guid.Empty)\n    {\n        return BadRequest("Invalid message.");\n    }\n\n    // Check if the message already exists in the database\n    var existingMessage = await _dbContext.Messages.FindAsync(message.Id);\n    if (existingMessage != null)\n    {\n        return Ok("Message already received.");\n    }\n\n    // Store the message in the database\n    _dbContext.Messages.Add(message);\n    await _dbContext.SaveChangesAsync();\n\n    return Ok("Message received and stored.");\n}