using System;\nusing System.Collections.Generic;\nusing System.IO;\n\npublic class FileTypeVerifier\n{\n    private static readonly Dictionary<string, byte[]> FileSignatures = new Dictionary<string, byte[]>\n    {\n        { "jpg", new byte[] { 0xFF, 0xD8, 0xFF } },\n        { "png", new byte[] { 0x89, 0x50, 0x4E, 0x47 } },\n        { "pdf", new byte[] { 0x25, 0x50, 0x44, 0x46 } },\n        // Add more file signatures as needed\n    };\n\n    public static string VerifyFileType(Stream fileStream)\n    {\n        byte[] headerBytes = new byte[8];\n        fileStream.Read(headerBytes, 0, headerBytes.Length);\n\n        foreach (var signature in FileSignatures)\n        {\n            if (headerBytes.Length >= signature.Value.Length)\n            {\n                bool isMatch = true;\n                for (int i = 0; i < signature.Value.Length; i++)\n                {\n                    if (headerBytes[i] != signature.Value[i])\n                    {\n                        isMatch = false;\n                        break;\n                    }\n                }\n\n                if (isMatch)\n                {\n                    return signature.Key;\n                }\n            }\n        }\n\n        return "unknown";\n    }\n}