using OpenQA.Selenium;\nusing OpenQA.Selenium.Support.UI;\nusing System;\n\npublic void SelectStateAfterCountry(IWebDriver driver)\n{\n    // Replace 'countryDropdownId' and 'stateDropdownId' with the actual IDs of your dropdowns\n    var countryDropdown = driver.FindElement(By.Id("countryDropdownId"));\n    var stateDropdown = driver.FindElement(By.Id("stateDropdownId"));\n    var wait = new WebDriverWait(driver, TimeSpan.FromSeconds(10));\n\n    // Select the country (example: USA)\n    var selectCountry = new SelectElement(countryDropdown);\n    selectCountry.SelectByText("USA");\n\n    // Wait for the state dropdown to be populated\n    wait.Until(d => stateDropdown.FindElements(By.TagName("option")).Count > 1);\n\n    // Now you can select a state\n    var selectState = new SelectElement(stateDropdown);\n    selectState.SelectByText("California");\n\n    // Continue with your test...\n}