using System;\nusing System.Text;\n\npublic class HexDecoder\n{\n    public static void Main()\n    {\n        string hexString = "0x4400450043004C00410052004500"; // Example string\n        string decodedString = DecodeHexString(hexString);\n        Console.WriteLine(decodedString);\n    }\n\n    public static string DecodeHexString(string hex)\n    {\n        // Remove the leading "0x" if present\n        if (hex.StartsWith("0x", StringComparison.OrdinalIgnoreCase))\n        {\n            hex = hex.Substring(2);\n        }\n\n        // Convert hex string to byte array\n        byte[] bytes = new byte[hex.Length / 2];\n        for (int i = 0; i < hex.Length; i += 2)\n        {\n            bytes[i / 2] = Convert.ToByte(hex.Substring(i, 2), 16);\n        }\n\n        // Convert byte array to string using Unicode encoding\n        return Encoding.Unicode.GetString(bytes);\n    }\n}