using System;\nusing System.Collections.Generic;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a sample DataSet\n        DataSet ds = new DataSet();\n        DataTable dt = new DataTable("SampleTable");\n        dt.Columns.Add("Column1", typeof(int));\n        dt.Columns.Add("Column2", typeof(string));\n        dt.Columns.Add("Column3", typeof(string));\n\n        // Add some sample rows\n        for (int i = 0; i < 600000; i++)\n        {\n            dt.Rows.Add(i, "Value" + (i % 1000), "OtherValue" + (i % 100));\n        }\n\n        ds.Tables.Add(dt);\n\n        // Create an index\n        Dictionary<Tuple<int, string>, List<DataRow>> index = new Dictionary<Tuple<int, string>, List<DataRow>>();\n\n        foreach (DataRow row in dt.Rows)\n        {\n            var key = Tuple.Create((int)row["Column1"], (string)row["Column2"]);\n            if (!index.ContainsKey(key))\n            {\n                index[key] = new List<DataRow>();\n            }\n            index[key].Add(row);\n        }\n\n        // Search using the index\n        var searchKey = Tuple.Create(123, "Value123");\n        if (index.TryGetValue(searchKey, out List<DataRow> resultRows))\n        {\n            foreach (DataRow resultRow in resultRows)\n            {\n                Console.WriteLine("Found Row: Column1={0}, Column2={1}, Column3={2}",\n                    resultRow["Column1"], resultRow["Column2"], resultRow["Column3"]);\n            }\n        }\n        else\n        {\n            Console.WriteLine("No matching rows found.");\n        }\n    }\n}