using System;\nusing System.Data.OleDb;\nusing System.Windows.Forms;\n\npublic partial class frmPin : Form\n{\n    static string connString = @"Provider=Microsoft.ACE.OLEDB.12.0;Data Source=C:\Users\Public\YourDatabase.accdb;Persist Security Info=True";\n    static private int pin = 11; // The First Pin in your records, for debugging inserted directly.\n\n    public frmPin()\n    {\n        InitializeComponent();\n    }\n\n    private void btnQry_Click(object sender, EventArgs e)\n    {\n        using (OleDbConnection conn = new OleDbConnection(connString))\n        {\n            string selectStatement = "SELECT Amount FROM Accounts WHERE PIN = ?";\n            using (OleDbCommand cmd = new OleDbCommand(selectStatement, conn))\n            {\n                cmd.Parameters.AddWithValue("@PIN", pin);\n\n                try\n                {\n                    conn.Open();\n                    using (OleDbDataReader reader = cmd.ExecuteReader())\n                    {\n                        if (reader.Read())\n                        {\n                            txtBx.Text = reader["Amount"].ToString();\n                        }\n                        else\n                        {\n                            txtBx.Text = "No records found.";\n                        }\n                    }\n                }\n                catch (Exception ex)\n                {\n                    txtBx.Text = "Exception: " + ex.Message;\n                }\n            }\n        }\n    }\n}