using System;\nusing System.Linq;\nusing System.Reflection;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load the assembly (replace with the path to your assembly)\n        Assembly assembly = Assembly.LoadFrom("YourAssembly.dll");\n\n        // Get all types in the assembly\n        Type[] types = assembly.GetTypes();\n\n        foreach (Type type in types)\n        {\n            // Check if the type is COM visible\n            ComVisibleAttribute comVisibleAttr = type.GetCustomAttributes(typeof(ComVisibleAttribute), false)\n                                                     .FirstOrDefault() as ComVisibleAttribute;\n\n            if (comVisibleAttr != null && comVisibleAttr.Value)\n            {\n                // Get ProgID if available\n                ProgIdAttribute progIdAttr = type.GetCustomAttributes(typeof(ProgIdAttribute), false)\n                                                 .FirstOrDefault() as ProgIdAttribute;\n\n                string progId = progIdAttr != null ? progIdAttr.Value : "No ProgID";\n\n                Console.WriteLine($"Type: {type.FullName}, ProgID: {progId}");\n            }\n        }\n    }\n}