using System;\n\nclass Program\n{\n    static void Main()\n    {\n        if (Console.IsOutputRedirected)\n        {\n            // Output is redirected, skip word-wrapping\n            Console.WriteLine("Output is redirected.");\n        }\n        else\n        {\n            // Output is not redirected, perform word-wrapping\n            Console.WriteLine("Output is not redirected.");\n            // Example word-wrapping logic based on Console.WindowWidth\n        }\n\n        if (Console.IsErrorRedirected)\n        {\n            // Error output is redirected\n            Console.WriteLine("Error output is redirected.");\n        }\n        else\n        {\n            // Error output is not redirected\n            Console.WriteLine("Error output is not redirected.");\n        }\n    }\n}