using System.Data;\nusing System.Data.OleDb;\n\npublic DataSet GetDataFromAccess()\n{\n    string connectionString = @"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=DatabaseFile.mdb;Persist Security Info=True";\n    string[] tableNames = { "Table1", "Table2", "Table3" };\n\n    DataSet ds = new DataSet();\n\n    using (OleDbConnection con = new OleDbConnection(connectionString))\n    {\n        con.Open();\n        using (OleDbDataAdapter da = new OleDbDataAdapter())\n        {\n            da.SelectCommand = new OleDbCommand();\n            da.SelectCommand.Connection = con;\n\n            foreach (string tableName in tableNames)\n            {\n                da.SelectCommand.CommandText = $"SELECT * FROM {tableName}";\n                da.Fill(ds, tableName);\n            }\n        }\n    }\n\n    return ds;\n}