using System;\n\npublic class FormatValidator\n{\n    public static bool ValidateFormatString(string formatString, params object[] args)\n    {\n        try\n        {\n            // Try to format the string with the provided arguments\n            string formattedString = String.Format(formatString, args);\n            return true; // If no exception, the format string is valid\n        }\n        catch (FormatException)\n        {\n            return false; // If a FormatException is thrown, the format string is invalid\n        }\n    }\n\n    public static void Main()\n    {\n        // Example usage\n        string format = "{0:dddd MMMM}";\n        object[] arguments = { 1234 }; // Number as argument\n\n        bool isValid = ValidateFormatString(format, arguments);\n\n        Console.WriteLine("Is the format string valid? " + isValid);\n    }\n}