// Ensure that both textboxes have their TextMode set to MultiLine in the .aspx file\n// <asp:TextBox ID="RawHTML" runat="server" TextMode="MultiLine" />\n// <asp:TextBox ID="BEHtml" runat="server" TextMode="MultiLine" />\n\nprotected void Button_Click(object sender, EventArgs e)\n{\n    // Encode the HTML to preserve formatting\n    string TransformHTML = Server.HtmlEncode(RawHTML.Text);\n    BEHtml.Text = TransformHTML;\n}