int[,] managed2DArray = new int[10, 10];\n   int size = managed2DArray.Length * sizeof(int);\n   IntPtr unmanagedArray = Marshal.AllocHGlobal(size);\n\n   // Copy managed 2D array to unmanaged memory\n   for (int i = 0; i < 10; i++) {\n       Marshal.Copy(managed2DArray, i * 10, unmanagedArray + i * 10 * sizeof(int), 10);\n   }\n\n   // Use unmanagedArray as needed...\n\n   // Free allocated memory\n   Marshal.FreeHGlobal(unmanagedArray);