using MsgReader.Outlook;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"path\to\your\email.msg";\n\n        using (var msg = new Storage.Message(filePath))\n        {\n            // Extracting fields\n            string from = msg.Sender.Email;\n            string to = string.Join(", ", msg.Recipients[Recipient.RecipientType.To].Select(r => r.Email));\n            string cc = string.Join(", ", msg.Recipients[Recipient.RecipientType.Cc].Select(r => r.Email));\n            string bcc = string.Join(", ", msg.Recipients[Recipient.RecipientType.Bcc].Select(r => r.Email));\n            DateTime? sentOn = msg.SentOn;\n            DateTime? receivedOn = msg.ReceivedOn;\n\n            // Output the extracted fields\n            Console.WriteLine("From: " + from);\n            Console.WriteLine("To: " + to);\n            Console.WriteLine("CC: " + cc);\n            Console.WriteLine("BCC: " + bcc);\n            Console.WriteLine("Sent On: " + sentOn?.ToString() ?? "N/A");\n            Console.WriteLine("Received On: " + receivedOn?.ToString() ?? "N/A");\n        }\n    }\n}