public static void Main()\n{\n    string username = "user";\n    string password = "pass";\n\n    // Generate a session key and IV\n    using (Aes aesAlg = Aes.Create())\n    {\n        aesAlg.GenerateKey();\n        aesAlg.GenerateIV();\n\n        byte[] key = aesAlg.Key;\n        byte[] iv = aesAlg.IV;\n\n        string encryptedUsername = EncryptionHelper.Encrypt(username, key, iv);\n        string encryptedPassword = EncryptionHelper.Encrypt(password, key, iv);\n\n        Console.WriteLine("Encrypted Username: " + encryptedUsername);\n        Console.WriteLine("Encrypted Password: " + encryptedPassword);\n\n        string decryptedUsername = EncryptionHelper.Decrypt(encryptedUsername, key, iv);\n        string decryptedPassword = EncryptionHelper.Decrypt(encryptedPassword, key, iv);\n\n        Console.WriteLine("Decrypted Username: " + decryptedUsername);\n        Console.WriteLine("Decrypted Password: " + decryptedPassword);\n    }\n}