using System;\nusing System.Web.UI;\n\npublic partial class Checkout : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (IsPostBack)\n        {\n            string selectedUnit = Request.Form["unitSelect"];\n            int measurement = int.Parse(Request.Form["measurementInput"]);\n            bool isValid = ValidateMeasurement(selectedUnit, measurement);\n\n            if (!isValid)\n            {\n                // Handle invalid input\n                Response.Write("<script>alert('Invalid measurement for the selected unit.');</script>");\n            }\n            else\n            {\n                // Proceed with form submission\n            }\n        }\n    }\n\n    private bool ValidateMeasurement(string unit, int measurement)\n    {\n        if (unit == "hands")\n        {\n            return measurement >= 15 && measurement <= 40;\n        }\n        else if (unit == "cm")\n        {\n            return measurement >= 200 && measurement <= 900;\n        }\n        return false;\n    }\n}