public int InsertPhoto(string fileName, string photoCaption, string photoDescription, bool isDefault, bool isPicture)\n{\n    using (var context = new MyDbContext())\n    {\n        var photo = new Photo\n        {\n            FileName = fileName,\n            PhotoCaption = photoCaption,\n            PhotoDescription = photoDescription,\n            Url = "http://www.myURL.com/photos/" + fileName,\n            IsDefault = isDefault,\n            IsPicture = isPicture\n        };\n        \n        context.Photos.Add(photo);\n        context.SaveChanges();\n        \n        return photo.Id;\n    }\n}