var query = context.Sales\n    .GroupBy(s => new { Year = s.OrderTime.Year, Month = s.OrderTime.Month })\n    .Select(g => new\n    {\n        Location1 = g.Where(s => s.Location == "Location1").Sum(s => s.Charge) ?? 0,\n        Location2 = g.Where(s => s.Location == "Location2").Sum(s => s.Charge) ?? 0,\n        Location3 = g.Where(s => s.Location == "Location3").Sum(s => s.Charge) ?? 0,\n        Date = new DateTime(g.Key.Year, g.Key.Month, 1) // Assuming you want the first day of the month\n    })\n    .OrderByDescending(r => r.Date)\n    .ToList();