using System.IO;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text;\n\npublic void DecryptPdf(string inputFile, string outputFile, string password)\n{\n    // Create a reader for the input PDF file\n    PdfReader reader = new PdfReader(inputFile, new System.Text.ASCIIEncoding().GetBytes(password));\n    \n    // Create a stamper to create the output PDF file\n    PdfStamper stamper = new PdfStamper(reader, new FileStream(outputFile, FileMode.Create));\n    \n    // Set the encryption to null (no encryption for the output file)\n    stamper.SetEncryption(null, null, 0, PdfWriter.STRENGTH128BITS);\n    \n    // Close the stamper and the reader\n    stamper.Close();\n    reader.Close();\n}