using System;\nusing System.IO;\n\npublic class Program\n{\n    public static void Main()\n    {\n        long targetSizeInBytes = 1_000_000_000; // 1GB\n        int bufferSize = 512 * 1024; // 512KB\n        byte[] buffer = new byte[bufferSize];\n        Random random = new Random();\n\n        using (FileStream fileStream = new FileStream("largeBinaryFile.bin", FileMode.Create, FileAccess.Write))\n        {\n            while (targetSizeInBytes > 0)\n            {\n                int sizeOfChunkToWrite = (int)Math.Min(bufferSize, targetSizeInBytes);\n\n                // Fill buffer with a mix of patterns and randomness\n                for (int i = 0; i < sizeOfChunkToWrite; i += 4)\n                {\n                    buffer[i] = (byte)random.Next(0, 256);\n                    buffer[i + 1] = (byte)random.Next(0, 256);\n                    buffer[i + 2] = 0xAA;\n                    buffer[i + 3] = 0x55;\n                }\n\n                fileStream.Write(buffer, 0, sizeOfChunkToWrite);\n                targetSizeInBytes -= sizeOfChunkToWrite;\n            }\n        }\n    }\n}