using System.Web;\nusing System.Web.Mvc;\n\npublic class FileUploadController : Controller\n{\n    [HttpPost]\n    public ActionResult UploadFile(HttpPostedFileBase file)\n    {\n        if (file != null && file.ContentLength > 0)\n        {\n            string fileType;\n            using (var fileStream = file.InputStream)\n            {\n                fileType = FileTypeVerifier.VerifyFileType(fileStream);\n            }\n\n            if (fileType == "unknown")\n            {\n                return new HttpStatusCodeResult(400, "Unsupported file type");\n            }\n\n            // Process the file as needed\n        }\n\n        return new HttpStatusCodeResult(200, "File uploaded successfully");\n    }\n}