// Controller action to handle file upload\n[HttpPost]\npublic async Task<IActionResult> UploadFile(IFormFile file)\n{\n    if (file == null || file.Length == 0)\n        return BadRequest("No file uploaded.");\n\n    // Define the shared directory path\n    string sharedDirectory = Path.Combine(@"C:\SharedFiles");\n    if (!Directory.Exists(sharedDirectory))\n    {\n        Directory.CreateDirectory(sharedDirectory);\n    }\n\n    // Create a unique file name to prevent collisions\n    string fileName = Guid.NewGuid().ToString() + Path.GetExtension(file.FileName);\n    string filePath = Path.Combine(sharedDirectory, fileName);\n\n    // Save the file to the shared directory\n    using (var stream = new FileStream(filePath, FileMode.Create))\n    {\n        await file.CopyToAsync(stream);\n    }\n\n    // Save the file path in the database\n    SaveFilePathToDatabase(filePath);\n\n    return Ok("File uploaded successfully.");\n}\n\nprivate void SaveFilePathToDatabase(string filePath)\n{\n    // Code to save the file path to the database\n    // Your data access logic here\n}