using System;\nusing System.IO;\nusing System.Security.AccessControl;\nusing System.Security.Principal;\n\npublic class FolderCreation\n{\n    public static void CreateFolderWithPermissions(string path)\n    {\n        // Create a DirectorySecurity object\n        DirectorySecurity security = new DirectorySecurity();\n\n        // Define the user (IUSR) and the rights (Modify)\n        string user = @"MACHINENAME\IUSR_MACHINENAME";\n        FileSystemRights rights = FileSystemRights.Modify;\n\n        // Create the access rule\n        FileSystemAccessRule accessRule = new FileSystemAccessRule(\n            user,\n            rights,\n            InheritanceFlags.ContainerInherit | InheritanceFlags.ObjectInherit,\n            PropagationFlags.None,\n            AccessControlType.Allow\n        );\n\n        // Add the access rule to the security object\n        security.AddAccessRule(accessRule);\n\n        // Create the directory with the specified security\n        DirectoryInfo directory = Directory.CreateDirectory(path, security);\n\n        Console.WriteLine("Directory created with specified permissions.");\n    }\n}