[WebMethod]\npublic static string GetGridViewData(int gridViewNumber)\n{\n    DataTable dt = new DataTable();\n    \n    // Populate the DataTable based on gridViewNumber.\n    // Example:\n    switch (gridViewNumber)\n    {\n        case 1:\n            // Populate dt for GridView1\n            break;\n        case 2:\n            // Populate dt for GridView2\n            break;\n        case 3:\n            // Populate dt for GridView3\n            break;\n    }\n\n    // Convert DataTable to JSON\n    string json = JsonConvert.SerializeObject(dt);\n    return json;\n}