using System;\nusing System.Diagnostics;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string serviceName = "YourServiceName"; // Replace with your actual service name\n\n        try\n        {\n            // Get the process ID for the service\n            ServiceController sc = new ServiceController(serviceName);\n            int processId = GetServiceProcessId(sc);\n\n            if (processId != 0)\n            {\n                Process serviceProcess = Process.GetProcessById(processId);\n                serviceProcess.Kill();\n                Console.WriteLine($"Service {serviceName} terminated successfully.");\n            }\n            else\n            {\n                Console.WriteLine("Service process ID not found.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n\n    static int GetServiceProcessId(ServiceController service)\n    {\n        int processId = 0;\n        using (ManagementObject wmiService = new ManagementObject($"Win32_Service.Name='{service.ServiceName}'"))\n        {\n            wmiService.Get();\n            processId = Convert.ToInt32(wmiService["ProcessId"]);\n        }\n        return processId;\n    }\n}