using System;\n   using System.Text;\n   using System.Security.Cryptography;\n\n   public class CredentialManager\n   {\n       public static void SaveCredentials(string appName, string userName, string password)\n       {\n           byte[] encryptedData = ProtectedData.Protect(\n               Encoding.UTF8.GetBytes(password),\n               Encoding.UTF8.GetBytes(appName),\n               DataProtectionScope.CurrentUser);\n\n           // Save encryptedData to a secure location\n       }\n\n       public static string RetrieveCredentials(string appName, string userName)\n       {\n           // Retrieve the encrypted data from secure location\n\n           byte[] decryptedData = ProtectedData.Unprotect(\n               encryptedData,\n               Encoding.UTF8.GetBytes(appName),\n               DataProtectionScope.CurrentUser);\n\n           return Encoding.UTF8.GetString(decryptedData);\n       }\n   }