public class MyComponent\n{\n    public void InitializeComponent()\n    {\n        if (ComInteropChecker.IsCalledFromCom())\n        {\n            // Use default configuration for COM clients\n            Console.WriteLine("Component invoked via COM interop. Using default configuration.");\n        }\n        else\n        {\n            // Use client's configuration\n            Console.WriteLine("Component invoked by .NET client. Using client's configuration.");\n        }\n    }\n}