using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            var fromAddress = new MailAddress("your-email@domain.com", "From Name");\n            var toAddress = new MailAddress("receiver-email@domain.com", "To Name");\n            const string fromPassword = "your-email-password";\n            const string subject = "Test Email Subject";\n            const string body = "This is a test email with HTML content and attachment.";\n\n            var smtp = new SmtpClient\n            {\n                Host = "127.0.0.1",\n                Port = 25,\n                EnableSsl = false,\n                DeliveryMethod = SmtpDeliveryMethod.Network,\n                UseDefaultCredentials = false,\n                Credentials = new NetworkCredential(fromAddress.Address, fromPassword)\n            };\n            using (var message = new MailMessage(fromAddress, toAddress)\n            {\n                Subject = subject,\n                Body = body,\n                IsBodyHtml = true\n            })\n            {\n                // Add an attachment if needed\n                Attachment attachment = new Attachment("path/to/your/file.txt");\n                message.Attachments.Add(attachment);\n\n                smtp.Send(message);\n            }\n            Console.WriteLine("Email sent successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to send email. " + ex.Message);\n        }\n    }\n}