using System;\nusing System.Runtime.InteropServices;\nusing System.Text;\n\nclass Program\n{\n    [StructLayout(LayoutKind.Sequential)]\n    private struct SHFILEINFO\n    {\n        public IntPtr hIcon;\n        public IntPtr iIcon;\n        public uint dwAttributes;\n        [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 260)]\n        public string szDisplayName;\n        [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 80)]\n        public string szTypeName;\n    };\n\n    private const uint SHGFI_TYPENAME = 0x000000400;\n    private const uint SHGFI_USEFILEATTRIBUTES = 0x000000010;\n\n    [DllImport("shell32.dll", CharSet = CharSet.Auto)]\n    private static extern IntPtr SHGetFileInfo(string pszPath, uint dwFileAttributes, ref SHFILEINFO psfi, uint cbFileInfo, uint uFlags);\n\n    public static string GetFileTypeDescription(string filePath)\n    {\n        SHFILEINFO shinfo = new SHFILEINFO();\n        uint fileAttribute = 0x80; // File attribute for normal files\n        IntPtr ptr = SHGetFileInfo(filePath, fileAttribute, ref shinfo, (uint)Marshal.SizeOf(shinfo), SHGFI_TYPENAME | SHGFI_USEFILEATTRIBUTES);\n\n        if (ptr != IntPtr.Zero)\n        {\n            return shinfo.szTypeName;\n        }\n\n        return null;\n    }\n\n    static void Main(string[] args)\n    {\n        string fileDescription = GetFileTypeDescription(".doc");\n        Console.WriteLine(fileDescription); // Output: Microsoft Word Document\n    }\n}