using System;\nusing TagLib;\n\nclass Program\n{\n    public static void GetVideoFileLength(string filePath)\n    {\n        var file = TagLib.File.Create(filePath);\n        if (file.Properties.MediaTypes == TagLib.MediaTypes.Video)\n        {\n            Console.WriteLine("Length: " + file.Properties.Duration);\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        GetVideoFileLength("example.avi");\n    }\n}