public void btn1_Click(object sender, EventArgs e)\n{\n    if (ddl1.SelectedValue == "1")\n    {\n        // Validate for numbers only\n        rev1.ValidationExpression = "^[0-9]+$";\n        rev1.ErrorMessage = "Please enter numbers only.";\n    }\n    else if (ddl1.SelectedValue == "2")\n    {\n        // Validate for letters only\n        rev1.ValidationExpression = "^[a-zA-Z]+$";\n        rev1.ErrorMessage = "Please enter letters only.";\n    }\n\n    // Manually validate the page\n    Page.Validate();\n\n    // Check if the page is valid\n    if (Page.IsValid)\n    {\n        // Your logic here if the input is valid\n        Response.Write("Input is valid!");\n    }\n    else\n    {\n        // Your logic here if the input is not valid\n        Response.Write("Input is not valid!");\n    }\n}