using System;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string rulesFile = "path_to_rules.xml";\n\n        XmlDocument doc = new XmlDocument();\n        doc.Load(rulesFile);\n\n        foreach (XmlNode ruleNode in doc.SelectNodes("/Rules/Rule"))\n        {\n            string ruleDescription = ruleNode.SelectSingleNode("Description").InnerText;\n            string ruleQuery = ruleNode.SelectSingleNode("Query").InnerText;\n\n            using (SqlConnection connection = new SqlConnection(connectionString))\n            {\n                SqlCommand command = new SqlCommand(ruleQuery, connection);\n                connection.Open();\n\n                SqlDataReader reader = command.ExecuteReader();\n                if (reader.HasRows)\n                {\n                    Console.WriteLine($"Rule Violation: {ruleDescription}");\n                    while (reader.Read())\n                    {\n                        // Process each row (for example, log it)\n                        Console.WriteLine($"Customer ID: {reader["CustomerID"]}, Cash%: {reader["CashPercentage"]}");\n                    }\n                }\n                reader.Close();\n            }\n        }\n    }\n}