using System;\nusing System.Data;\nusing System.IO;\nusing System.Xml;\n\npublic class XmlToDataSetConverter\n{\n    public DataSet ConvertXmlDocumentToDataSet(XmlDocument xmlDocument)\n    {\n        if (xmlDocument == null)\n        {\n            throw new ArgumentNullException(nameof(xmlDocument));\n        }\n\n        DataSet dataSet = new DataSet();\n        using (StringReader stringReader = new StringReader(xmlDocument.OuterXml))\n        {\n            dataSet.ReadXml(stringReader);\n        }\n\n        return dataSet;\n    }\n}