using iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic void MergeDocuments(List<string> filePaths, string outputPdfPath)\n{\n    using (FileStream stream = new FileStream(outputPdfPath, FileMode.Create))\n    {\n        Document pdfDoc = new Document();\n        PdfCopy pdfCopy = new PdfCopy(pdfDoc, stream);\n        pdfDoc.Open();\n\n        foreach (string filePath in filePaths)\n        {\n            PdfReader reader = new PdfReader(filePath);\n            for (int page = 1; page <= reader.NumberOfPages; page++)\n            {\n                pdfCopy.AddPage(pdfCopy.GetImportedPage(reader, page));\n            }\n            reader.Close();\n        }\n\n        pdfDoc.Close();\n    }\n}