using System;\nusing System.Diagnostics;\nusing System.Net.NetworkInformation;\nusing System.Threading;\n\npublic class NetworkMonitor\n{\n    private PerformanceCounter bytesSentCounter;\n    private PerformanceCounter bytesReceivedCounter;\n\n    public NetworkMonitor(string networkInterfaceName)\n    {\n        bytesSentCounter = new PerformanceCounter("Network Interface", "Bytes Sent/sec", networkInterfaceName);\n        bytesReceivedCounter = new PerformanceCounter("Network Interface", "Bytes Received/sec", networkInterfaceName);\n    }\n\n    public void DisplayNetworkUsage()\n    {\n        while (true)\n        {\n            float bytesSent = bytesSentCounter.NextValue();\n            float bytesReceived = bytesReceivedCounter.NextValue();\n            \n            Console.WriteLine($"Bytes Sent: {bytesSent} B/s");\n            Console.WriteLine($"Bytes Received: {bytesReceived} B/s");\n            \n            Thread.Sleep(1000); // Update every second\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        var interfaces = NetworkInterface.GetAllNetworkInterfaces();\n        foreach (var ni in interfaces)\n        {\n            Console.WriteLine(ni.Name);\n        }\n\n        Console.Write("Enter the network interface name to monitor: ");\n        string networkInterfaceName = Console.ReadLine();\n\n        NetworkMonitor monitor = new NetworkMonitor(networkInterfaceName);\n        monitor.DisplayNetworkUsage();\n    }\n}