using System;\nusing System.Web.UI.HtmlControls;\n\nnamespace YourNamespace\n{\n    public partial class Default : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (!IsPostBack)\n            {\n                // Example collection of items\n                var items = new[]\n                {\n                    new { Text = "do foo", ImageUrl = "some_image.png" },\n                    new { Text = "do bar", ImageUrl = "some_image.png" }\n                };\n\n                // Add items to the list\n                foreach (var item in items)\n                {\n                    HtmlGenericControl li = new HtmlGenericControl("li");\n                    li.InnerText = item.Text + " ";\n\n                    HtmlAnchor anchor = new HtmlAnchor();\n                    anchor.HRef = "#"; // Set the hyperlink URL\n\n                    HtmlImage img = new HtmlImage();\n                    img.Src = item.ImageUrl; // Set the image source\n\n                    anchor.Controls.Add(img);\n                    li.Controls.Add(anchor);\n\n                    dynamicList.Controls.Add(li); // Add the list item to the UL\n                }\n            }\n        }\n    }\n}