using System;\nusing System.Threading;\n\nclass Program\n{\n    private static readonly ManualResetEventSlim shutdownEvent = new ManualResetEventSlim(false);\n\n    static void Main(string[] args)\n    {\n        // Handle the CancelKeyPress event to shut down gracefully\n        Console.CancelKeyPress += new ConsoleCancelEventHandler(OnCancelKeyPress);\n\n        Console.WriteLine("Application is running. Press Ctrl+C to exit.");\n\n        // Wait for the shutdown event to be set\n        shutdownEvent.Wait();\n\n        // Perform any cleanup or final actions here\n        Console.WriteLine("Application is shutting down...");\n    }\n\n    private static void OnCancelKeyPress(object sender, ConsoleCancelEventArgs e)\n    {\n        // Prevent the process from terminating immediately\n        e.Cancel = true;\n\n        // Signal the shutdown event\n        shutdownEvent.Set();\n    }\n}