double CalculateOpacityIncrement(int speed)\n{\n    // Ensure speed is within the range [1, 10]\n    if (speed < 1) speed = 1;\n    if (speed > 10) speed = 10;\n\n    // Quadratic mapping from speed to opacity increment\n    double minIncrement = 0.005;\n    double maxIncrement = 0.1;\n    double incrementRange = maxIncrement - minIncrement;\n\n    // Normalize the speed to a range of 0 to 1\n    double normalizedSpeed = (speed - 1) / 9.0;\n\n    // Apply quadratic function (you can adjust the exponent to control the curve)\n    double opacityIncrement = minIncrement + incrementRange * Math.Pow(normalizedSpeed, 2);\n\n    return opacityIncrement;\n}