using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a sample DataTable\n        DataTable table = new DataTable();\n        table.Columns.Add("Column1", typeof(string));\n        table.Columns.Add("Column2", typeof(int));\n\n        DataRow row = table.NewRow();\n        row["Column1"] = "Sample Data";\n        row["Column2"] = DBNull.Value;\n        table.Rows.Add(row);\n\n        // Check if a column exists and is not DBNull\n        string columnName = "Column2";\n        if (table.Columns.Contains(columnName))\n        {\n            if (row.IsNull(columnName))\n            {\n                Console.WriteLine($"Column '{columnName}' exists but is DBNull.");\n            }\n            else\n            {\n                Console.WriteLine($"Column '{columnName}' exists and has value: {row[columnName]}");\n            }\n        }\n        else\n        {\n            Console.WriteLine($"Column '{columnName}' does not exist.");\n        }\n    }\n}