using MailKit.Net.Smtp;\nusing MimeKit;\n\npublic void SendEmail(string from, string to, string subject, string content, string username, string password)\n{\n    var message = new MimeMessage();\n    message.From.Add(new MailboxAddress("Sender Name", from));\n    message.To.Add(new MailboxAddress("Recipient Name", to));\n    message.Subject = subject;\n\n    message.Body = new TextPart("plain")\n    {\n        Text = content\n    };\n\n    using (var client = new SmtpClient())\n    {\n        // For demo-purposes, accept all SSL certificates (in case the server supports STARTTLS)\n        client.ServerCertificateValidationCallback = (s, c, h, e) => true;\n\n        client.Connect("smtp.example.com", 465, true);\n        client.Authenticate(username, password);\n\n        client.Send(message);\n        client.Disconnect(true);\n    }\n}