using System;\nusing System.Runtime.InteropServices;\n\npublic class ComInteropChecker\n{\n    public static bool IsCalledFromCom()\n    {\n        try\n        {\n            // If the current application domain's context is COM, \n            // the Marshal.GetTypeFromCLSID method should work without throwing an exception.\n            Type comType = Marshal.GetTypeFromCLSID(Guid.Empty);\n            return comType != null;\n        }\n        catch (COMException)\n        {\n            // If a COMException is thrown, this indicates that the call is not from COM.\n            return false;\n        }\n        catch (Exception)\n        {\n            // Handle other exceptions if necessary\n            return false;\n        }\n    }\n}