using System;\nusing System.Windows.Forms;\nusing NHotkey;\nusing NHotkey.WindowsForms;\n\nnamespace HotkeyAppLauncher\n{\n    static class Program\n    {\n        [STAThread]\n        static void Main()\n        {\n            Application.EnableVisualStyles();\n            Application.SetCompatibleTextRenderingDefault(false);\n\n            // Register the hotkey\n            try\n            {\n                HotkeyManager.Current.AddOrReplace("LaunchApp", Keys.Control | Keys.W | Keys.W, OnHotkeyPressed);\n            }\n            catch (Exception ex)\n            {\n                MessageBox.Show("Failed to register hotkey: " + ex.Message);\n            }\n\n            Application.Run();\n        }\n\n        private static void OnHotkeyPressed(object sender, HotkeyEventArgs e)\n        {\n            // Launch your application\n            System.Diagnostics.Process.Start("your-application.exe");\n            e.Handled = true;  // Mark the event as handled\n        }\n    }\n}