using System;\nusing System.Collections.Generic;\nusing System.Runtime.InteropServices;\nusing Excel = Microsoft.Office.Interop.Excel;\n\nnamespace ExcelController\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // List to keep track of Excel instances\n            List<Excel.Application> excelApps = new List<Excel.Application>();\n\n            // Start a few instances of Excel\n            for (int i = 0; i < 3; i++)\n            {\n                Excel.Application excelApp = new Excel.Application();\n                excelApp.Visible = true;  // Make Excel visible for debugging purposes\n                excelApps.Add(excelApp);\n            }\n\n            // Perform some operations on each Excel instance\n            foreach (var excelApp in excelApps)\n            {\n                Excel.Workbook workbook = excelApp.Workbooks.Add();\n                Excel.Worksheet worksheet = (Excel.Worksheet)workbook.Sheets[1];\n                worksheet.Cells[1, 1] = "Hello, Excel!";\n            }\n\n            // Stop all Excel instances\n            foreach (var excelApp in excelApps)\n            {\n                excelApp.Quit();\n                Marshal.ReleaseComObject(excelApp);\n            }\n\n            // Clear the list\n            excelApps.Clear();\n\n            Console.WriteLine("All Excel instances have been managed successfully.");\n        }\n    }\n}