using System;\nusing System.Data;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = @"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=yourdatabase.mdb;";\n        \n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            connection.Open();\n\n            // Get the schema information for the tables\n            DataTable schemaTable = connection.GetOleDbSchemaTable(OleDbSchemaGuid.Tables, null);\n\n            DataSet dataSet = new DataSet();\n\n            foreach (DataRow row in schemaTable.Rows)\n            {\n                string tableName = row["TABLE_NAME"].ToString();\n                \n                // Skip system tables\n                if (row["TABLE_TYPE"].ToString() == "TABLE")\n                {\n                    OleDbDataAdapter adapter = new OleDbDataAdapter($"SELECT * FROM [{tableName}]", connection);\n                    adapter.Fill(dataSet, tableName);\n                }\n            }\n\n            connection.Close();\n\n            // Display the data in the DataSet (for example purposes)\n            foreach (DataTable table in dataSet.Tables)\n            {\n                Console.WriteLine($"Table: {table.TableName}");\n                foreach (DataRow dataRow in table.Rows)\n                {\n                    foreach (var item in dataRow.ItemArray)\n                    {\n                        Console.Write($"{item} ");\n                    }\n                    Console.WriteLine();\n                }\n                Console.WriteLine();\n            }\n        }\n    }\n}