using System;\nusing Ganss.XSS;\n\nclass Program\n{\n    static void Main()\n    {\n        string htmlInput = "<p>This is a <a href=\"http://example.com\">link</a>. <br> <script>alert('XSS');</script></p>";\n\n        // Create an instance of HtmlSanitizer\n        var sanitizer = new HtmlSanitizer();\n\n        // Optionally, configure allowed tags and attributes\n        sanitizer.AllowedTags.Add("a");\n        sanitizer.AllowedTags.Add("br");\n        sanitizer.AllowedTags.Add("p");\n\n        sanitizer.AllowedAttributes.Add("href");\n\n        // Sanitize the HTML input\n        string sanitizedHtml = sanitizer.Sanitize(htmlInput);\n\n        Console.WriteLine(sanitizedHtml);\n    }\n}