using System;\nusing System.Collections.Generic;\n\npublic class ArrayMixer\n{\n    public static List<T> MixArrays<T>(T[] array1, T[] array2)\n    {\n        List<T> mixedArray = new List<T>();\n        int maxLength = Math.Max(array1.Length, array2.Length);\n\n        for (int i = 0; i < maxLength; i++)\n        {\n            if (i < array1.Length)\n            {\n                mixedArray.Add(array1[i]);\n            }\n            if (i < array2.Length)\n            {\n                mixedArray.Add(array2[i]);\n            }\n        }\n\n        return mixedArray;\n    }\n\n    public static void Main(string[] args)\n    {\n        char[] array1 = { 'A', 'B', 'C', 'D', 'E', 'F', 'G' };\n        int[] array2 = { 1, 2, 3, 4 };\n\n        List<object> mixedArray = MixArrays<object>(array1.Cast<object>().ToArray(), array2.Cast<object>().ToArray());\n\n        foreach (var item in mixedArray)\n        {\n            Console.Write(item + " ");\n        }\n    }\n}